import { useState } from "react";

const Forms = (props) => {
  const [requiredInputs, setRequiredInputs] = useState(props.objArr);

  const handleChange = (event) => {
    console.log(event.target.name, event.target.value);
  }

  const allRequiredInputs = requiredInputs.map((e, index) => {
    return (
      <tr key={index}>
        <td id={index}>{e.name}</td>
        <td>{e.buydate}{e.buydate === null && <input name={index} type="text" value={e.buydate} onChange={handleChange} />}</td>
        <td>{e.buydate !== null && "$" + e.buyprice.toFixed(2)}</td>
        <td>{e.selldate}</td>
        <td>{e.selldate !== null && "$" + e.sellprice.toFixed(2)}</td>
      </tr>
    );
  });

  return (
    <div className="formtable">
      <div>
        <h2>Please input damage & heals here</h2>
      </div>
      <form>
        <table>
          <tr>
            <th>Name</th>
            <th>Purchase Date</th>
            <th>Damage</th>
            <th>Sell Date</th>
            <th>Heal</th>
          </tr>
          {allRequiredInputs}
        </table>
      </form>
    </div>
  );
};

export default Forms;
